class Pelanggan_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    function countPelanggan($idDagangan){
        return $this->db->query("SELECT * FROM PELANGGAN WHERE PELANGGAN.idDagangan = '$idDagangan'")->num_rows();
    }

    function isBerlangganan($idDagangan, $idPembeli){
        if($this->db->query("SELECT * FROM PELANGGAN WHERE PELANGGAN.idDagangan = '$idDagangan' AND 
                            PELANGGAN.idPembeli = '$idPembeli'")->num_rows()>0)
            return true;
        else
            return false;

    }

    function addPelanggan($idPembeli, $idDagangan, $waktuBerlangganan){
        $this->db->query("INSERT INTO `PELANGGAN`(`idPembeli`, `idDagangan`, `waktuBerlangganan`) VALUES ('$idPembeli','$idDagangan','$waktuBerlangganan')");
    }

    function deletePelanggan($idPembeli, $idDagangan){
        $this->db->query("DELETE FROM `PELANGGAN` WHERE idPembeli = '$idPembeli' AND idDagangan = '$idDagangan'");
    }
}
